import * as callActions from '../actions/callActions';

const initState = {
    localStream:null,
    callState:callActions
};


const reducer = (state = initState, action) => {
    switch (action.type) {
        case callActions.CALL_SET_LOCAL_STREAM:
            return {
                ...state,
               localStream:action.localStream
            };

       
        default:
            return state;
    }
}


export default reducer;
